def processData(s) 
    @length = s.length;
    p @length
    @s=s

    def reverseString(str) 
        return str.split('').reverse().join('');
    end
    def cal(index,s) 
        
        if index > 1 
            d = s.length - index;
            notfound = true
            for i in 0..d 
            #for i = 0; i <= d; i++ do
                s1 = s[i, index];
                sr1 = reverseString(s1);
                if (s1 == sr1) 
                    notfound = false
                    p s1.length;
                    break;
                end
            end
            if notfound 
                index = index - 1;
                cal(index,s)
            end
        end
    end

    def checkpal(s) 
        p s
        p Time.now();
        sr = reverseString(s);
        if s == sr
            p s.length
        else 
            cal(s.length - 1,s)
        end
    end


    def pal(nxt) 
       checkpal(nxt)
            if @length > 1 
                nxt = nxt[1, @s.length] + nxt[0]
                @length = @length - 1
                pal(nxt)
            end
       
    end
    pal(s)
end
processData("")
